settings.custom_css:
:root{
    --expanding-color: #F42E79;
}
selector{
    cursor: pointer;
}
selector .elementor-divider-separator:before{
    content: "";
    position: absolute;
    top: calc(50% - 14px);
    left: calc(50% - 8px);
    border: 25px solid #fff;
    border-top: 15px solid transparent;
    border-bottom: 15px solid transparent;
    border-right: 0 solid transparent;
    transition: all 0.3s ease-in-out;
}
selector.active .elementor-divider-separator:before{
    opacity: 0;
}
selector .elementor-divider-separator:after{
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    background: var(--expanding-color);
    height: 100%;
    width: 100%;
    z-index: -1;
    border-radius: 100%;
    transform: translate(0, 15px);
    filter: blur(12px) opacity(0.4);
    transition: all 0.5s ease-in-out;
}
selector.active .elementor-divider-separator:after{
    filter: blur(0) opacity(1);
    transform: translate(0,0) scale(30);
    transition: all 0.5s ease-in-out, filter 0.3s ease-in-out;
}
selector + *{
    transition: all 0.3s ease-in-out;
}
selector.active + *{
    opacity: 0;
}

selector{
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    pointer-events: none;
    max-width: 100vw;
}
selector.active{
    pointer-events: initial;
    background: var(--expanding-color);
    transition: all 0.2s ease-in-out 0.3s;
}
selector .elementor-container,
selector .elementor-column,
selector .elementor-column,
selector .elementor-widget-wrap{
    position: static;
}
selector .v-video{
    transition: all 0.5s ease-in-out;
    opacity: 0;
    transform: scale(0.5);
}
selector.active .v-video{
    transition: all 0.5s ease-in-out 0.2s;
    transform: scale(1);
    opacity: 1;
}
selector .v-close{
    transition: all 0.5s ease-in-out;
    opacity: 0;
    cursor: pointer;
}
selector.active .v-close{
    opacity: 1;
    transition: all 0.5s ease-in-out 0.5s;
}

selector{
    width: 70px;
    position: absolute;
    top: 55px;
    right: 40px;
}

@media (max-width: 767px){
selector{
    width: 60px;
    top: 25px;
    right: 10px;
}
}
